package pkg1;

public class p10 {
	public static int add(int a,int b) {
		return a+b;
	}
	public static int mul(int a,int b) {
		return a*b;
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x,y;
		x=add(15,20);
		System.out.println("Sum : "+x);
		y=mul(15,20);
		System.out.println("Product : "+y);

	}

}
